import React, { useState, useEffect } from 'react';
import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Title from './Title';
import Probar from './Probar';
import { Box, LinearProgress } from '@material-ui/core';

import Moment from 'react-moment';
import 'moment/locale/ko';
import Clock from 'react-live-clock';
import hostdata from "../hostData.json"
import CurrentTast from './CurrentTask';

const useStyles = makeStyles({
    depositContext: {
        flex: 1,
    },
});

const nowWorking = (status) => {
    if (status === 'a') {
        return (
            "주차장에서 하적장으로 이동하고 있습니다."
        )
    }
    else if (status === 'b') {
        return (
            <span>"하적장에서 창고로 이동하고 있습니다."</span>
        )
    }
    else if (status === 'c') {
        return (
            <span>"창고에서 하적장으로 이동하고 있습니다."</span>
        )
    }
    else if (status === 'd') {
        return (
            <span>"창고에서 주차장으로 이동하고 있습니다."</span>
        )
    }
    else if (status === 'e') {
        return (
            <span>"하적장에서 주차장으로 이동하고 있습니다."</span>
        )
    }
    else if (status === 'x') {
        return (
            <span>"짐을 싣는 중입니다."</span>
        )
    }
    else if (status === 'y') {
        return (
            <span>"짐을 내리는 중입니다."</span>
        )
    }
    else if (status === 'z') {
        return (
            <span>"회차하는 중입니다."</span>
        )
    }
}

export default function Work(departure, destination) {
    const classes = useStyles();

    return (
        <React.Fragment>
            <Title>Work</Title>
            <Box marginTop={3}>
                <Typography component="p" variant="h5">
                    현재 작업
                </Typography>
                <Typography color="textSecondary" className={classes.depositContext} variant="h6">
                    <CurrentTast>hostdata.status</CurrentTast>
                </Typography>
                <Box marginTop={1} width="50%">
                    <LinearProgress color={'secondary'} />
                </Box>
            </Box>
            <Box marginTop={3}>
                <Typography component="p" variant="h5">
                    전체 작업
                </Typography>
                <Typography color="textSecondary" className={classes.depositContext} variant="h6">
                    {"하적장 --> 창고"}
                </Typography>
                <Probar />
            </Box>

        </React.Fragment>
    );
}

Work.defaultProps = {
    data: 'B',
    departure: '하적장',
    destination: '창고',
}
