import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles, ThemeProvider } from '@material-ui/core/styles';
import BatteryStdIcon from '@material-ui/icons/BatteryStd';
import AssignmentIcon from '@material-ui/icons/Assignment';
import { createMuiTheme } from '@material-ui/core/styles';
import { amber, yellow } from '@material-ui/core/colors';
import Copyright from './components/Copyright';

const primary = createMuiTheme({
    main: '#ffc107',
    light: '#ffcd38',
    dark: '#b28704',
});
const secondary = createMuiTheme({
    main: '#ffeb3b',
    light: '#ffef62',
    dark: '#b2a429',
});

const theme = createMuiTheme({
    palette: {
        primary: amber,
        secondary: yellow,
    },
});

const useStyles = makeStyles((theme) => ({
    root: {
        height: '100vh',
        backgroundColor: theme.palette.grey[100],
    },
    image: {
        backgroundImage: 'url(http://hd18116987.com/images/main/main01.png)',
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
    },
    main: {
        margin: theme.spacing(8, 4),
        display: 'flex',
        flexDirection: 'column',
        //alignItems: 'center',

    },
    avatar: {
        backgroundColor: secondary.main,
        top: '3rem',
        marginLeft: 15,

        //backgroundColor: theme.palette.primary.dark,
    },
    start: {
        backgroundColor: primary,
        margin: theme.spacing(3, 0, 2),
        padding: theme.spacing(1),
        fontSize: '2rem',
    },
    comp: {
        fontSize: 20,
        margin: theme.spacing(4, 1),
        padding: theme.spacing(2),
        textAlign: 'center',
        //color: theme.palette.primary,
        //윗줄 되는건지 의심스러움
    }
}));

export default function Welcome({ name, location, status, battery }) {
    const classes = useStyles();
    return (

        <Grid container component="main" className={classes.root}>
            <CssBaseline />
            <Grid item xs={false} sm={4} md={7} component={Paper} elevation={6} square className={classes.image} />

            <Grid item xs={12} sm={8} md={5} >
                <div className={classes.main}>

                    <Typography align="left" variant="h3">
                        {'어서오세요'}
                        <br />
                        {name}
                        {' 님'}
                    </Typography>

                    <Grid container spacing={3}>
                        <Grid item xs>
                            <Avatar variant="rounded" relative className={classes.avatar}>
                                <AssignmentIcon />
                            </Avatar>
                            <Paper className={classes.comp} location={location}>{location}</Paper>
                        </Grid>
                        <Grid item xs>
                            <Paper className={classes.comp}>{status}</Paper>
                        </Grid>
                        <Grid item xs>
                            <Paper className={classes.comp}>
                                <BatteryStdIcon fontSize="small" />
                                {battery}{"%"}</Paper>
                        </Grid>
                    </Grid>

                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        className={classes.start}
                    >
                        작업시작
                    </Button>
                    <Box mt={5}>
                        <Copyright />
                    </Box>
                </div>
            </Grid>
        </Grid>
    );
}

Welcome.defaultProps = {
    name: "지범",
    location: '작업장',
    status: '작업중',
    battery: '68',
}
