import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles, ThemeProvider } from '@material-ui/core/styles';
import BatteryStdIcon from '@material-ui/icons/BatteryStd';
import { createMuiTheme } from '@material-ui/core/styles';
import { amber, grey, yellow } from '@material-ui/core/colors';
import Copyright from './components/Copyright';
import EventSeatIcon from '@material-ui/icons/EventSeat';
import RoomIcon from '@material-ui/icons/Room';

const primary = createMuiTheme({
    main: '#ffc107',
    light: '#ffcd38',
    dark: '#b28704',
});
const secondary = createMuiTheme({
    main: '#ffeb3b',
    light: '#ffef62',
    dark: '#',
});

const theme = createMuiTheme({
    palette: {
        primary: amber,
        secondary: yellow,
    },
});
const initialState = {
    count: 0,
    show: true
};
const reload = () => {
    setState({ show: false })
    setTimeout(() => setState(state => ({ count: state.count + 1, show: true })), 500)
}

const useStyles = makeStyles((theme) => ({
    root: {
        height: '100vh',
        backgroundColor: theme.palette.grey[100],
    },
    image: {
        backgroundImage: 'url(http://hd18116987.com/images/main/main01.png)',
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
    },
    main: {
        margin: theme.spacing(8, 4),
        display: 'flex',
        flexDirection: 'column',
        //alignItems: 'center',
    },
    avatar: {
        padding: theme.spacing(3),
        backgroundColor: primary.main,
        top: '4.3rem',
        marginLeft: '5rem',
        //backgroundColor: theme.palette.primary.dark,
    },
    start: {
        margin: theme.spacing(10, 5),
        padding: theme.spacing(1),
        fontSize: '2rem',
        backgroundColor: 'primary'
    },
    comp: {
        fontSize: '2rem',
        padding: theme.spacing(3),
        textAlign: 'center',
        //color: theme.palette.primary,
        //윗줄 되는건지 의심스러움
    },
    stat: {
        margin: theme.spacing(2),
        component: 'div',
    }
}));

export default function Welcome({ name, location, status, battery }) {
    const classes = useStyles();
    return (
        <ThemeProvider theme={theme}>
            <Grid container component="main" className={classes.root}>
                <CssBaseline />
                <Grid item xs={false} sm={4} md={7} component={Paper} elevation={6} square className={classes.image} />

                <Grid item xs={12} sm={8} md={5} >
                    <Grid className={classes.main}>

                        <Typography align="left" variant="h3">
                            {'어서오세요'}
                            <br />
                            {name}
                            {' 님'}
                        </Typography>

                        <Grid component="div" className={classes.stat}>
                            <Grid item xs>
                                <Avatar relative className={classes.avatar}>
                                    <RoomIcon style={{ color: grey[900] }} />
                                </Avatar>
                                <Paper className={classes.comp} location={location}>{'위치: '}{location}</Paper>
                            </Grid>

                            <Grid item xs>
                                <Avatar variant="rounded" relative className={classes.avatar}>
                                    <EventSeatIcon style={{ color: grey[900] }} />
                                </Avatar>
                                <Paper className={classes.comp}>
                                    {'상태: '}{status}</Paper>
                            </Grid>

                            <Grid item xs>
                                <Avatar variant="rounded" relative className={classes.avatar}>
                                    <BatteryStdIcon style={{ color: grey[900] }} />
                                </Avatar>
                                <Paper className={classes.comp}>
                                    {'연료: '}
                                    {battery}{"%"}</Paper>
                            </Grid>
                        </Grid>

                        <Button
                            type="submit"
                            variant="contained"
                            color="secondary"
                            className={classes.start}
                        >
                            작업시작
                        </Button>

                        <Box>
                            <Typography variant="body2" color="textSecondary" align="center">
                                {'POTATO Folk Lift Self Driving System'}
                                <br />
                                {'Copyright © POTATO'}
                                {' '}
                                {new Date().getFullYear()}
                                {'.'}
                            </Typography>
                        </Box>
                    </Grid>
                </Grid>
            </Grid>
        </ThemeProvider>
    );
}

Welcome.defaultProps = {
    name: "지범",
    location: '작업장',
    status: '작업중',
    battery: '68',
}
