import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import SettingsIcon from '@material-ui/icons/Settings';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import { typography } from '@material-ui/system';
import BatteryStdIcon from '@material-ui/icons/BatteryStd';

const useStyles = makeStyles((theme) => ({
    root: {
        height: '100vh',
        backgroundColor: theme.palette.grey[100],
    },
    image: {
        backgroundImage: 'url(http://hd18116987.com/images/main/main01.png)',
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
    },
    paper: {
        margin: theme.spacing(8, 4),
        display: 'flex',
        flexDirection: 'column',
        //alignItems: 'center',

    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main,
    },
    start: {
        margin: theme.spacing(3, 0, 2),

    },
    comp: {
        margin: theme.spacing(4, 1),
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    }
}));

export default function Welcome({ name, location, status, battery }) {
    const classes = useStyles();
    return (
        <Grid container component="main" className={classes.root}>
            <CssBaseline />
            <Grid item xs={false} sm={4} md={7} component={Paper} elevation={6} square className={classes.image} />
            <Grid item xs={12} sm={8} md={5} >
                <div className={classes.paper}>
                    <Avatar className={classes.avatar}>
                        <SettingsIcon fontSize="large" />
                    </Avatar>

                    <Typography align="left" variant="h3">
                        {'어서오세요'}
                        <br />
                        {name}
                        {' 님'}
                    </Typography>

                    <Grid container spacing={3}>
                        <Grid item xs>
                            <Paper className={classes.comp} location={location}>{location}</Paper>
                        </Grid>
                        <Grid item xs>
                            <Paper className={classes.comp}>{status}</Paper>
                        </Grid>
                        <Grid item xs>
                            <Paper className={classes.comp}>
                                <BatteryStdIcon fontSize="small" />{'Baterry'}<br />
                                {battery}{"%"}</Paper>
                        </Grid>
                    </Grid>

                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="primary"
                        className={classes.start}
                    >
                        작업시작
                    </Button>
                    <Box mt={5}>
                        <Copyright />
                    </Box>
                </div>
            </Grid>
        </Grid>
    );
}


Welcome.defaultProps = {
    name: "지범",
    location: '작업장',
    status: '작업중',
    battery: '68',
}


function Copyright() {
    return (
        <>
            <Typography variant="body2" color="textSecondary" align="center">
                {'Folk Lift Self Driving System'}
                <br />
                {'Copyright © POTATO'}
                {' '}
                {new Date().getFullYear()}
                {'.'}
            </Typography>


        </>

    );
}
