import React, { useState, useEffect } from 'react';
import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Title from './Title';
import Probar from './Probar';
import { Box, LinearProgress } from '@material-ui/core';

import Moment from 'react-moment';
import 'moment/locale/ko';
import Clock from 'react-live-clock';
import axios from 'axios';

const useStyles = makeStyles({
    depositContext: {
        flex: 1,
    },
});

export default function Work(departure, destination) {
    const classes = useStyles();
    const [track, setTrack] = useState('');
    useEffect(() => {
        let repeat;

        async function fetchData() {
            try {
                const res = await fetch("/Users/woojibeom/Documents/potato/src/hostData.json");
                const json = await res.json();

                setTrack(json.status);

                repeat = setTimeout(fetchData, 7000); //7초 리핏
            } catch (error) {
                console.error(error.message)
            }
        }
        fetchData();
        return () => {
            if (repeat) {
                clearTimeout(repeat);
            }
        }
    }, []);

    const hostdata = axios.get('');
    return (
        <React.Fragment>
            <Title>Work</Title>
            <Typography color="textSecondary" variant="body1">
                진행시간 (파이썬, react-live-clock, react-moment로 시작 경과 시간)
            </Typography>
            <Box marginTop={3}>
                <Typography component="p" variant="h5">
                    현재 작업
                </Typography>
                <Typography color="textSecondary" className={classes.depositContext} variant="h6">
                    {track}
                </Typography>
                <Box marginTop={1} width="50%">
                    <LinearProgress color={'secondary'} />
                </Box>
            </Box>
            <Box marginTop={3}>
                <Typography component="p" variant="h5">
                    전체 작업
                </Typography>
                <Typography color="textSecondary" className={classes.depositContext} variant="h6">
                    {"하적장 --> 창고"}
                </Typography>
                <Probar />
            </Box>

        </React.Fragment>
    );
}

Work.defaultProps = {
    data: 'B',
    departure: '하적장',
    destination: '창고',
}
